SELECT A.PRODUCTNO , A.PRODUCTTYPE , A.PRODUCTNAME , A.PRICE , A.STOCK , A.INFO , A.RNUM FROM ( SELECT PRODUCTNO , PRODUCTTYPE , PRODUCTNAME , PRICE , STOCK , INFO , ROWNUM RNUM FROM PRODUCTS
WHERE PRODUCTTYPE = #{producttype}
ORDER BY PRODUCTTYPE DESC ) A
WHERE A.RNUM BETWEEN #{strNum} AND #{endNum}
