mapper.select:
SELECT COUNT(*)
  FROM REVIEWLIST
 WHERE PRODUCTNO = #{productno}

SELECT REVIEWID, PRODUCTNO, ID, REVIEW, R_REG
  FROM ( SELECT ROWNUM AS RN, B.* 
		   FROM ( SELECT *
		   			FROM REVIEWLIST
		   		   ORDER BY REVIEWID DESC
		   		) B	  
  		)
 WHERE RN BETWEEN #{begin} AND ${end}

SELECT REVIEWID, PRODUCTNO, ID, REVIEW, R_REG
  FROM REVIEWLIST
 WHERE REVIEWID = #{reviewId}
